import React from 'react';
import { Link } from "react-router-dom";

class OrderForm extends React.Component {
  // state = {};
  // handleChange = e => {
  //   // console.log({value: e.target.value})

  //   this.setState({
  //     [e.target.name]: e.target.value,
  //   })
  // };

  render() {
    return(
      <div>
        <form >
          <div className="form-group">
            <label>Order Code</label>
            <input 
              onChange={this.props.onChange} 
              className="form-control" 
              type="text" 
              name="orderCode"
              value={this.props.formValues.order_code}
            />
          </div>

          <div className="form-group">
            <label>Order Name</label>
            <input 
              onChange={this.props.onChange} 
              className="form-control" 
              type="text" 
              name="orderName"
              value={this.props.formValues.order_name}
            />
          </div>

          <div className="form-group">
            <label>Order App</label>
            <input 
              onChange={this.props.onChange} 
              className="form-control" 
              type="text" 
              name="orderApp"
              value={this.props.formValues.order_app}
            />
          </div>

        </form>
        <Link to="/orders/" onClick={this.handleClick} className="btn btn-primary">Save</Link>
        <Link to="/orders/" className="btn btn-primary">Ir al home</Link>
      </div>
    )
  }
};

export default OrderForm;
