import React from 'react';

class OrderForm extends React.Component {
  // state = {};
  // handleChange = e => {
  //   // console.log({value: e.target.value})

  //   this.setState({
  //     [e.target.name]: e.target.value,
  //   })
  // };

  handleClick = e => {
    e.preventDefault()
    this.setState({ loading: true, error: null })
    alert('A form was submitted')

    try {
      const url = 'http://localhost:3000/orders';
      const data = {
        orderStatus:this.state.form.orderStatus, 
        orderCode:this.state.form.orderCode,
        orderName:this.state.form.orderName,
        orderApp:this.state.form.orderApp
      }
      fetch(url, { method: 'POST', // or ‘PUT’
        body: JSON.stringify(data), // data can be `string` or {object}!
        headers:{ 'Content-Type': 'application/json' } })
      this.setState({ loading: false })
    } catch (error) {
      this.setState({ loading: false, error: error })
    }
  };


  render() {
    return(
      <div>
        <h1>New order</h1>

        <form onSubmit={this.props.onSubmit}>
          <div className="form-group">
            <label>Order Code</label>
            <input 
              onChange={this.props.onChange} 
              className="form-control" 
              type="text" 
              name="orderCode"
              value={this.props.formValues.orderCode}
            />
          </div>

          <div className="form-group">
            <label>Order Name</label>
            <input 
              onChange={this.props.onChange} 
              className="form-control" 
              type="text" 
              name="orderName"
              value={this.props.formValues.orderName}
            />
          </div>

          <div className="form-group">
            <label>Order App</label>
            <input 
              onChange={this.props.onChange} 
              className="form-control" 
              type="text" 
              name="orderApp"
              value={this.props.formValues.orderApp}
            />
          </div>

        </form>
        <button onClick={this.handleClick} className="btn btn-primary">Save</button>
      </div>
    )
  }
};

export default OrderForm;
