import React from 'react';
import Order from '../components/Order'
import '../assets/styles/OrderEdit.css';
import OrderForm from '../components/OrderForm';
import Navbar from '../components/Navbar'

class OrderEdit extends React.Component {
  state = { 
    loading: true,
    error: null,
    form: {
    order_code: '',
    order_app: '',
    order_name: '',
  } };

  componentDidMount () {
    this.fetchData();
  }

  fetchData = async () => {
    this.setState({ loading: true, error: null})

    try {
      const id = this.props.match.params.orderId
      const url = `http://localhost:3000/orders/${id}`;
      const response = await fetch(url);
      const data = await response.json();
      this.setState({loading: false, form: data})
    } catch (error){
      this.setState({loading: false, error: error})
    }
  }


  handleChange = e => {
    this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value,
      }
    })
  }


  handleClick = e => {
    alert('An order was submitted')
    const object = {
      "order": {
        "order_status": "Pending",
        "order_app": this.props.formValues.orderApp,
        "order_code": this.props.formValues.orderCode,
        "order_name": this.props.formValues.orderName,
      }
    };


    fetch('http://localhost:3000/orders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        // We convert the React state to JSON and send it as the POST body
        body: JSON.stringify(object)
      }).then(function(response) {
        console.log(response)
        return response.json();
      });
    console.log(object);
    e.preventDefault();
  };

  render() {
    return (
      <div className="order-edit">
        <h1>Edit an order</h1>
        <Navbar />
        <div className="container">
          <div className="row">
            <div className="col-6">
              <h1>Order Preview</h1>
              <Order 
                orderStatus={this.state.form.order_status || "Pending"} 
                orderCode={this.state.form.order_code || "Code"} 
                orderName={this.state.form.order_name || "Name"} 
                orderApp={this.state.form.order_app || "App"} 
               />
            </div>

            <div className="col-6">
              <h1>Edit order</h1>
              <OrderForm 
                onChange={this.handleChange}
                onClick={handleClick}
                formValues={this.state.form}
              />
            </div>
          </div>
        </div>
      </div>
    )
  }
};

export default OrderEdit;
